ulate_answers_%POLL-ID%();\r\n	if(typeof window.tabulate_results_%POLL-ID% == \\"function\\")  \r\n		tabulate_results_%POLL-ID%();\r\n});\r\n\r\nfunction equalWidth_%POLL-ID%(obj, cols, findWidest ) {\r\n	findWidest  = typeof findWidest  !== \\"undefined\\" ? findWidest  : false;\r\n	if ( findWidest ) {\r\n		obj.each(function() {\r\n			var thisWidth = jQuery(this).width();\r\n			width = parseInt(thisWidth / cols); \r\n			jQuery(this).width(width);    \r\n			jQuery(this).css(\\"float\\", \\"left\\");    \r\n		});\r\n	}\r\n	else {\r\n		var widest = 0;\r\n		obj.each(function() {\r\n			var thisWidth = jQuery(this).width();\r\n			if(thisWidth > widest) {\r\n				widest = thisWidth; \r\n			}    \r\n		});\r\n		width = parseInt( widest / cols); \r\n		obj.width(width);    \r\n		obj.css(\\"float\\", \\"left\\");    \r\n	}    \r\n}\r\n\r\nfunction tabulate_answers_%POLL-ID%() {\r\n	equalWidth_%POLL-ID%( jQuery(\\"#shivs-poll-container-%POLL-ID% .shivs-poll-li-answer-%POLL-ID%\\"), %ANSWERS-TABULATED-COLS% );\r\n	//equalWidth_%POLL-ID%( jQuery(\\"#shivs-poll-container-%POLL-ID% .shivs-poll-li-answer-%POLL-ID% .shivs-poll-results-bar-%POLL-ID% div \\"), %ANSWERS-TABULATED-COLS%, true );\r\n}\r\n\r\nfunction tabulate_results_%POLL-ID%() {\r\n	equalWidth_%POLL-ID%( jQuery(\\"#shivs-poll-container-%POLL-ID% .shivs-poll-li-result-%POLL-ID%\\"), %RESULTS-TABULATED-COLS% );\r\n	//equalWidth_%POLL-ID%( jQuery(\\"#shivs-poll-container-%POLL-ID% .shivs-poll-li-result-%POLL-ID% .shivs-poll-results-bar-%POLL-ID% div \\"), %RESULTS-TABULATED-COLS%, true );\r\n	}\r\n\r\njQuery(document).ready(function(){\r\n	runOnPollStateChange_%POLL-ID%();\r\n});\r\n\r\nfunction runOnPollStateChange_%POLL-ID%() {\r\n\r\n};', 'default', '2014-10-10 20:06:57', '2014-10-09 13:18:06'),
